import { createSlice } from "@reduxjs/toolkit";

const categoriesSlice = createSlice({
  name: 'categories',
  initialState: {
    categoriesList: [],
  },
  reducers: {
    gettingCategories(state, action) {
      console.log(state.categoriesList);
      console.log(action.payload)
      // state = action.payload;
      // console.log(state)
    }
  }
})
export const categoriesActions = categoriesSlice.actions;

export const gettingCategories = () => {
  console.log('getting categories function')
  return async (dispatch) => {
    console.log('async')
    const  getCategories = async() => {
      const response = await fetch('https://api.thecatapi.com/v1/categories');
      const categories = await response.json();

      return categories;
    }

    try {
      const x = await getCategories();
      console.log('try', x);
      dispatch(categoriesActions.gettingCategories())
    } catch(error) {
      console.log(error)
    }
  }
}


export default categoriesSlice;
