import React, { useEffect, useState } from 'react';
import './styles.css'

const Cats = () => {
  const [catsList, setCatsList] = useState([]);
  const [categories, setCategories]  = useState([]);
  useEffect(()=> {
  async function getCatsInfo() {
    try {
      let response = await fetch('https://api.thecatapi.com/v1/images/search?limit=10&page=1&category_ids=1');
      let catsInfo = await response.json();
      setCatsList(catsInfo);
    } catch(error) {
     console.log(error);
    }
  }

  async function getCategories() {
    try {
      let response = await fetch('https://api.thecatapi.com/v1/categories');
      let categories = await response.json();
      setCategories(categories);
    } catch(error) {
     console.log(error);
    }
  }

  getCategories();
  getCatsInfo()
  }, [])

  useEffect(()=>{
    console.log(catsList);
    console.log(categories);
  })

  return (
    <div className="cats-wrapper">
      <ul>
        {catsList.map((cat)=>{
          return (
            <li key={cat.id}>
              <img src={cat.url} alt="" />
            </li>
          )
        })}
        <li></li>
      </ul>
    </div>
  )
}

export default Cats;
