import React, { useEffect, useState } from 'react';
import './styles.scss';
import { useSelector, useDispatch } from 'react-redux';
import { categoriesActions } from '../../store/reducers/categoriesSlice';
import { gettingCategories } from '../../store/actions/categoriesActions';
import { gettingCats } from '../../store/actions/catsActions';
import { Link } from 'react-router-dom';

const Sidebar = () => {
  const dispatch = useDispatch();

  useEffect(()=> {
    dispatch(gettingCategories());
  }, [dispatch])

  const { categoriesList, categoriesStatus, currentCategory } = useSelector(state => state.categories);
  const [categories, setCategories] = useState([]);

  useEffect(() => {
    if(categoriesStatus === 'success') {
      setCategories(categoriesList);
    }
  }, [categoriesStatus, categoriesList])

  const setCurrentCategory = (id) => {
    dispatch(categoriesActions.setCurrentCategory(id));
    dispatch(gettingCats(id));
  }

  return (
    <div className="sidebar-wrapper">
      <h6 className="sidebar-title">categories</h6>
      <ul>
        {categories?.map((category)=>{
            const { id, name } = category;
            return (
              <li key={id}>
                <Link
                  to={`/category?categoryId`}
                  className={`${currentCategory === id && 'current_category'} category-btn`}
                  onClick={()=>setCurrentCategory(id)}
                >
                  {name}
                </Link>
              </li>
            )
          })}
      </ul>
    </div>
  )
}

export default Sidebar;
