import { API_LINK } from "../../Helpers/constants";
import { catsActions } from "../reducers/catsSlice";

export const gettingCats = (limit,categoryId, pageNumber) => {
  return async (dispatch) => {
    dispatch(catsActions.setStatus('pending'));
    const getCats = async() => {
      const response = await fetch(
        `${API_LINK}/images/search?limit=10&page=${pageNumber}&category_ids=${categoryId}`
      );
      const catsList = await response.json();

      return catsList;
    }

    try {
      const cats = await getCats();
      dispatch(catsActions.gettingCatsList(cats));
      dispatch(catsActions.setStatus('success'));
    } catch(error) {
      dispatch(catsActions.setStatus('error'));
    }
  }
}
