import { createAsyncThunk, createSlice } from '@reduxjs/toolkit';
import { API_LINK } from '../../helpers/constants';

export const gettingCategoriesX = () => createAsyncThunk('cart/gettingCategories', () => {
  console.log('ppp');

  return fetch(`${API_LINK}/categories`)
    .then(response => response.json())
    .catch(error => console.log(error));
});

const categoriesSlice = createSlice({
  name: 'categories',
  initialState: {
    categoriesList: [],
    categoriesStatus: 'pending',
  },
  reducers: {
    gettingCategories(state, action) {
      state.categoriesList = [...action.payload];
    },
    setStatus(state, action) {
      state.categoriesStatus = action.payload;
    },
  },
  extraReducers: {
    [gettingCategoriesX.pending]: (state) => state.categoriesStatus = 'pending',
    [gettingCategoriesX.fulfilled]: (state, { payload }) => {
      state.categoriesStatus = 'success';
      state.categoriesList = payload;
    },
    [gettingCategories.rejected]: (state) => state.categoriesStatus = 'error',
  }
});

export const categoriesActions = categoriesSlice.actions;
export default categoriesSlice;
