import React from 'react';
import './styles.scss';
import routeItems from '../../helpers/constants/routeItems';
import { Link } from 'react-router-dom';

const Sidebar = () => {
  const isCurrent = '/courses';

  return (
    <div className="sidebar-wrapper">
      <ul className="sidebar-list-container">
        {
          routeItems.map((item) => {
            return (
              <li key={item.name} className={`${isCurrent === item.path ? 'active' : ''}`}>
                <Link to={item.path}>
                  <div className="sidebar-icon">{item.icon()} {item.name}</div>

                </Link>
              </li>
            );
          })
        }
      </ul >
    </div >
  );
};

export default Sidebar;
