  const counter = useSelector(state => state.counter);
  const dispatch = useDispatch();

  const increament = ()=>{
    dispatch({type: 'INC'})
  }
  const decreament = ()=>{
    dispatch({type: 'DEC'})
  }

  const addBy = ()=>{
    dispatch({type: 'ADD', payload: 10});
  }

  const [catsList, setCatsList] = useState([]);
