import React from 'react';
import './styles.scss';
import routeItems from '../../helpers/constants/routeItems';
import { Link } from 'react-router-dom';
import { useSelector } from 'react-redux';

const Sidebar = () => {
  const isCurrent = '/courses';
  const currentRoute = useSelector(state => state.routes);

  console.log(currentRoute.name);

  return (
    <div className="sidebar-wrapper">
      <ul className="sidebar-list-container">
        {
          routeItems.map((item) => {
            return (
              <li key={item.key} className={`${isCurrent === item.path ? 'active' : ''}`}>
                <Link to={item.path}>
                  {/* <div className="sidebar-icon">{item.icon()} {item.name}</div> */}
                  <div>{item.path} {isCurrent}</div>
                </Link>
              </li>
            );
          })
        }
      </ul >
    </div >
  );
};

export default Sidebar;
