import { useSelector } from 'react-redux';
import './App.scss'
import Sidebar from './components/Sidebar/Sidebar';
import Cats from './components/Cats/Cats';
import Spinner from './components/Spinner/Spinner';
import {
  BrowserRouter as Router, Switch, Route, Redirect,
} from 'react-router-dom';

export const Routes: RouteType[] = [
  {
    isNavBar: true,
    isExact: true,
    path: '/',
    name: 'Login',
    component: LoginContainer,
  },
  {
    isNavBar: true,
    isExact: true,
    path: '/control/home',
    name: 'Home',
    component: HomeContainer,
  },
  {
    isNavBar: true,
    isExact: true,
    path: '/login',
    name: 'Login',
    component: LoginContainer,
  },
  {
    isNavBar: true,
    isExact: true,
    path: '/control/roadmap/groups',
    name: 'RoadmapGroupsContainer',
    component: RoadmapGroupsContainer,
  },
  {
    isNavBar: true,
    isExact: true,
    isPrivate: true,
    path: '/control/dashboard',
    name: 'dashboard',
    component: DashboardContainer,
  },
  {
    isNavBar: true,
    isExact: true,
    isPrivate: true,
    path: '/control/users',
    name: 'user',
    component: UsersComponent,
  },
  {
    isNavBar: true,
    isExact: true,
    isPrivate: true,
    path: '/control/view',
    name: 'dashboard',
    component: UsersComponent,
  },
  {
    isNavBar: true,
    isExact: true,
    isPrivate: true,
    path: '/control/roadmap/stack/:id/:sectionId',
    name: 'Roadmap Section View',
    component: RoadmapSectionViewContainer,
  },
  {
    isNavBar: true,
    isExact: true,
    isPrivate: true,
    path: '/users/view',
    name: 'UserWievContainer',
    component: UserWievContainer,
  },
  {
    isNavBar: true,
    isExact: true,
    isPrivate: true,
    path: '/content/courses',
    name: 'CoursesContainer',
    component: CoursesContainer,
  },
  // {
  //   isNavBar: true,
  //   isExact: true,
  //   isPrivate: true,
  //   path: '/contents/courses/create',
  //   name: 'CoursesContainer',
  //   component: CoursesCreateContainer,
  // },
  {
    isNavBar: true,
    isExact: true,
    isPrivate: true,
    path: '/content/sections',
    name: 'SectionContainer',
    component: SectionContainer,
  },
  {
    isNavBar: true,
    isExact: true,
    isPrivate: true,
    path: '/contents/section/create',
    name: 'SectionContainer',
    component: SectionCreateContainer,
  },
  // {
  //   isNavBar: true,
  //   isExact: true,
  //   isPrivate: true,
  //   path: '/contents/courses/edit',
  //   name: 'SectionContainer',
  //   component: CoursesEditContainer,
  // },
  {
    isNavBar: true,
    isExact: true,
    isPrivate: true,
    path: '/contents/section/edit',
    name: 'SectionContainer',
    component: SectionEditContainer,
  },
  {
    isNavBar: true,
    isExact: true,
    isPrivate: true,
    path: '/content/lessons',
    name: 'LessonsContainer',
    component: LessonsContainer,
  },
];


function App() {
  const { categoriesStatus } = useSelector(state => state.categories);

  return (
    <Router>
       <Switch>
          <div className="App">
            {categoriesStatus === 'pending' &&  <Spinner />}
            <div className="app-wrapper">
              {/* <Sidebar />
              <Cats /> */}
                     {Routes.map((route) => {

              <Route key={route.name} path={route.path} component={route.component} exact={route.isExact} />
          })}
            </div>
          </div>
       </Switch>
    </Router>
  );
}

export default App;
