import { useSelector } from 'react-redux';
import './App.scss'
import Sidebar from './components/Sidebar/Sidebar';
import Cats from './components/Cats/Cats';
import Spinner from './components/Spinner/Spinner';
import {
  BrowserRouter as Router, Routes, Route, Link,
} from 'react-router-dom';
import Home from './components/Home/Home';

function App() {
  const { categoriesStatus } = useSelector(state => state.categories);
  const routes = useSelector(state => state.routes);
  const routesList = useSelector(state => state.categories?.categoriesList)

  console.log(routesList );

  return (
    <>
     <div>
        {/* {categoriesStatus === 'pending' &&  <Spinner />}
        <div className="app-wrapper">
          <Sidebar />
        </div> */}
        home
        <Link to='/category'>Home</Link>
      </div>
      <Router>
        <Route 
              key='home'
              path='/'
              component={<Home /> }
              exact
            />
        {
          routesList?.map((route) => {
            console.log(route.id);
            <Route 
              key={route.id} 
              path={`/category/categoryId=${route.id}`} 
              component={<Cats /> }
              exact
            />
          })
        }
      </Router>
    </>

  );
}

export default App;
