import { useSelector } from 'react-redux';
import './App.scss'
import Sidebar from './components/Sidebar/Sidebar';
import Cats from './components/Cats/Cats';
import Spinner from './components/Spinner/Spinner';
import {
  BrowserRouter as Router, Routes, Route,
} from 'react-router-dom';

function App() {
  const { categoriesStatus } = useSelector(state => state.categories);
  const routes = useSelector(state => state.routes);
  const routesList = useSelector(state => state.categories)

  console.log(routes.currentRoute, routesList );

  return (
    <Router>
          <div className="App">
            {categoriesStatus === 'pending' &&  <Spinner />}
            <div className="app-wrapper">
              <Sidebar />
                {routesList.map((route) => {
                  <Route 
                    key={route.id} 
                    path={route.path} 
                    component={<Cats 
                  /> 
                  }
                   exact={route.isExact} />
                })}
              {/* <Routes>
                <Route path="/" element={<Cats /> } />
                <Route path="/acc" element={<Cats /> } />
              </Routes> */}
            </div>
          </div>
    </Router>
  );
}

export default App;
