import { createSlice } from '@reduxjs/toolkit';

const coursesSlice = createSlice({
  name: 'course',
  initialState: {
    coursesList: [],
    flitersList: ['popular', 'favorite', 'new'],
    filterBy: 'popular'
  },
  reducers: {
    setFilterBy(state, { payload }) {
      state.filterBy = payload;
    },
    setCoursesList(state, {payload}) {
      if(payload.filterB)
      state.coursesList = payload;
    }
  }
});

export const { setFilterBy } = coursesSlice.actions;
export default coursesSlice;
