import { createAsyncThunk, createSlice } from '@reduxjs/toolkit';
import { API_LINK } from '../../helpers/constants';

export const gettingCategories = () => createAsyncThunk('cart/gettingCategories', () => {
  return fetch(`${API_LINK}/categories`)
    .then(response => response.json())
    .catch(error => console.log(error));
});

const categoriesSlice = createSlice({
  name: 'categories',
  initialState: {
    categoriesList: [],
    categoriesStatus: 'pending',
  },
  reducers: {
    gettingCategories(state, action) {
      state.categoriesList = [...action.payload];
    },
    setStatus(state, action) {
      state.categoriesStatus = action.payload;
    },
  },
  extraReducers: {
    [gettingCategories.pending]: (state) => state.categoriesStatus = 'pending',
    [gettingCategories.fulfilled]: (state) => state.categoriesStatus = 'pending',
    [gettingCategories.error]: (state) => state.categoriesStatus = 'pending',

  }
});

export const categoriesActions = categoriesSlice.actions;
export default categoriesSlice;
