import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import routeItems from '../../helpers/constants/routeItems';
import { setRoute } from '../../store/slices/routesSlice';
import Header from '../Header/Header';
import Sidebar from '../Sidebar/Sidebar';
import './styles.scss';

// eslint-disable-next-line react/prop-types
const Main = ({ children }) => {
  const dispatch = useDispatch();
  const path = window.location.pathname;

  if (path) {
    const currentRouteInfo = routeItems.find((route) => route.path === path);
    // console.log(currentRouteInfo);
    dispatch(setRoute({
      key: currentRouteInfo.key,
      title: currentRouteInfo.title,
    }));
  }
  const currentPath = useSelector(state => state.routes);
  console.log(currentPath);

  return (
    <div className="main-wrapper">
      <Header />
      <Sidebar />
      <div className="component-wrapper">
        {children}
      </div>
    </div>
  );
};

export default Main;
