import { useEffect, useState } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import './App.css'
import Sidebar from './components/Sidebar/Sidebar';
import Cats from './components/Cats/Cats';



function App() {
  const counter = useSelector(state => state.counter);
  const dispatch = useDispatch();

  const increament = ()=>{
    dispatch({type: 'INC'})
  }
  const decreament = ()=>{
    dispatch({type: 'DEC'})
  }

  const addBy = ()=>{
    dispatch({type: 'ADD', payload: 10});
  }

  const [catsList, setCatsList] = useState([]);
  const [categories, setCategories]  = useState([]);
  useEffect(()=> {
  async function getCatsInfo() {
    try {
      let response = await fetch('https://api.thecatapi.com/v1/images/search?limit=10&page=1&category_ids=1');
      let catsInfo = await response.json();
      setCatsList(catsInfo);
    } catch(error) {
     console.log(error);
    }
  }

  async function getCategories() {
    try {
      let response = await fetch('https://api.thecatapi.com/v1/categories');
      let categories = await response.json();
      setCategories(categories);
    } catch(error) {
     console.log(error);
    }
  }

  getCategories();
  getCatsInfo()
  }, [])

  useEffect(()=>{
    console.log(catsList);
    console.log(categories);
  })



  return (
    <div className="App">
      <h1>Counter App</h1>
      <h2>{counter}</h2>
      <button onClick={increament}>Increament</button>
      <button onClick={decreament}>Decreament</button>
      <button onClick={addBy}>AddBy 10</button>

      <div className="app-wrapper">

        <Sidebar />
        <Cats />

        {/* <ul>
          {catsList.map((cat)=>{
            return (
              <li key={cat.id}>
                <img src={cat.url} alt="" />
              </li>
            )
          })}
          <li></li>
        </ul> */}
      </div>
    </div>
  );
}

export default App;
