import { createSlice } from "@reduxjs/toolkit";

const categoriesSlice = createSlice({
  name: 'categories',
  initialState: {
    categoriesList: null,
    categoriesStatus: null,
    currentCategory: null,
  },
  reducers: {
    gettingCategories(state, action) {
      state.categoriesList = [...action.payload];
    },
    setStatus(state, action) {
      state.categoriesStatus = action.payload;
    },
    setCurrentCategory(state, action) {
      state.currentCategory = action.payload;
    }
  }
})
export const categoriesActions = categoriesSlice.actions;

export const gettingCategories = () => {
  return async (dispatch) => {
    dispatch(categoriesActions.setStatus('pending'));
    const  getCategories = async() => {
      const response = await fetch('https://api.thecatapi.com/v1/categories');
      const categories = await response.json();

      return categories;
    }

    try {
      const categoriesList = await getCategories();
      dispatch(categoriesActions.gettingCategories(categoriesList));
      dispatch(categoriesActions.setStatus('success'));
    } catch(error) {
      dispatch(categoriesActions.setStatus('error'));
    }
  }
}


export default categoriesSlice;
