import React from 'react';
import { useDispatch, useSelector } from 'react-redux';
import cubesList from '../../helpers/constants/data';
import { setFilterBy } from '../../store/slices/coursesSlice.';
import Card from './Card';
import './styles.scss';

const Courses = () => {
  const dispatch = useDispatch();
  const { coursesList,flitersList, filterBy } = useSelector((state) => state.courses);

  return (
    <div className="courses-page-container">
      <div className="courses-page-top">
        <h3 className="page-main-title">Courses</h3>
        <div className="filter-btn-container">
          {flitersList.map((filter) => (
            <button
              key={filter}
              className={`${filterBy === filter ? 'active ' : ''}filter-btn`}
              onClick={() => { dispatch(setFilterBy(filter)); }}
            >
              {filter}
            </button>
          ))}
        </div>
      </div>
      <div>
        {coursesList.map((card) => {
          return (
            <div key={card.id}>
              <Card {...card} />
            </div>
          );
        })}
      </div>
    </div >
  );
};

export default Courses;
