import React, { useEffect, useState } from 'react';
import './styles.css'

const Cats = () => {
  const [catsList, setCatsList] = useState([]);

  useEffect(()=> {
  async function getCatsInfo() {
    try {
      let response = await fetch('https://api.thecatapi.com/v1/images/search?limit=10&page=1&category_ids=1');
      let catsInfo = await response.json();
      setCatsList(catsInfo);
    } catch(error) {
     console.log(error);
    }
  }

  async function filterByCategory() {
    try {
      let response = await fetch('https://api.thecatapi.com/images/search?category_ids=1');
      let catsInfo = await response.json();
      setCatsList(catsInfo);
    } catch(error) {
     console.log(error);
    }
  }

  getCatsInfo()
  }, [])

  useEffect(()=>{
    console.log(catsList);
  })

  return (
    <div className="cats-wrapper">
      <ul>
        {catsList.map((cat)=>{
          return (
            <li key={cat.id}>
              <div className="cat-img-container">
                <img src={cat.url} alt="" />
              </div>
            </li>
          )
        })}
      </ul>
    </div>
  )
}

export default Cats;
