/* eslint-disable react/prop-types */
import React from 'react';
import './styles.scss';

const Card = ({ name, bgColor, quantity, views }) => {
  return (
    <div className="card-container" style={{ background: `${bgColor}` }}>
      <div className="card-content">
        <h6>{name}</h6>
        <h6>{quantity}</h6>
        <h6>{views}</h6>
      </div>
    </div>
  );
};

export default Card;
