import { createSlice } from '@reduxjs/toolkit';

const coursesSlice = createSlice({
  name: 'course',
  initialState: {
    coursesList: [],
    flitersList: ['popular', 'favorite', 'new'],
    filterBy: 'popular'
  },
  reducers: {
    setFilterBy(state, { payload }) {
      state.filterBy = payload;
    },
    setCoursesList(state, { payload }) {
      if (payload.filterBy === 'popular') {
        const x = payload?.list?.sort((item1, item2) => item2.views - item2.views);

        console.log(x);
      }
      state.coursesList = payload;
    }
  }
});

export const { setFilterBy, setCoursesList } = coursesSlice.actions;
export default coursesSlice;
