import { URL } from "../../Helpers/constants";
import { catsActions } from "../reducers/catsSlice";

export const gettingCats = (categoryId) => {
  return async (dispatch) => {
    dispatch(catsActions.setStatus('pending'));
    const getCats = async() => {
      const response = await fetch(`${API_LINK}/images/search?limit=10&page=2&category_ids=2`);
      const catsList = await response.json();

      return catsList;
    }

    try {
      const cats = await getCats();
      dispatch(catsActions.gettingCatsList(cats));
      dispatch(catsActions.setStatus('success'));
    } catch(error) {
      dispatch(catsActions.setStatus('error'));
    }
  }
}
